import React, { useState } from 'react';
import Alert from 'react-bootstrap/Alert';
import Fade from 'react-bootstrap/Fade';

function AlertMobile() {

    const [show, setShow] = useState(true);

    //  if (show) {   
    //     return (
    //         <Alert variant = "warning" transition={Fade} onClose={() => setShow(false)} dismissible >
    //             <Alert.Heading>Hi there, thanks for viewing my website!</Alert.Heading>
    //             <p>The mobile version of this website has not been implemented yet due to lack of free time.</p>
    //             <hr />
    //             <p className="mb-0">
    //                 I apologize for the inconvenience.
    //             </p>
    //         </Alert>
    //     );
    // }     

    return (
        <>
          <Alert show={show} variant="success">
            <Alert.Heading>How's it going?!</Alert.Heading>
            <p>
              Duis mollis, est non commodo luctus, nisi erat porttitor ligula, eget
              lacinia odio sem nec elit. Cras mattis consectetur purus sit amet
              fermentum.
            </p>
            <hr />
            <div className="d-flex justify-content-end">
              <Button onClick={() => setShow(false)} variant="outline-success">
                Close me y'all!
              </Button>
            </div>
          </Alert>
    
          {!show && <Button onClick={() => setShow(true)}>Show Alert</Button>}
        </>
      );
}

export default AlertMobile;
