import React, { useState } from 'react';
import Alert from 'react-bootstrap/Alert';
import Button from 'react-bootstrap/Button';

function AlertMobile() {

    const [show, setShow] = useState(true);

    //  if (show) {   
    //     return (
    //         <Alert variant = "warning" transition={Fade} onClose={() => setShow(false)} dismissible >
    //             <Alert.Heading>Hi there, thanks for viewing my website!</Alert.Heading>
    //             <p>The mobile version of this website has not been implemented yet due to lack of free time.</p>
    //             <hr />
    //             <p className="mb-0">
    //                 I apologize for the inconvenience.
    //             </p>
    //         </Alert>
    //     );
    // }     

    return (
        <>
          <Alert show={show} variant="warning">
            <Alert.Heading>Hi there, thanks for viewing my website!</Alert.Heading>
            <p>
            The mobile version of this website has not been implemented yet due to lack of free time.
            </p>
            <hr />
            <div className="d-flex justify-content-start">
                <Button onClick={() => setShow(false)} variant="outline-warning">
                    Close Alert
                </Button>
            </div>
          </Alert>

        </>
      );
}

export default AlertMobile;
