import React from 'react';
import { useEffect, useState } from 'react';
import { Carousel, Container } from 'react-bootstrap';
import './Style.css';
import Resume from './../img/pallotta_resume.pdf';

function ContentContainer({ navSelected }) {

    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    }

    useEffect(() => {
        var divContainer = document.getElementById("content_container");
        var divs = divContainer.getElementsByClassName("contentDivs");

        for (var i = 0; i < divs.length; i++) {
            if (divs[i].dataset.item !== navSelected) {
                divs[i].classList.add("hiddenDivs");
                
            }

            else {
                divs[i].classList.remove("hiddenDivs");
                divs[i].style.color = "red"
            }
        }
      });

    return (
        <Container id="content_container">
            <div data-item="home" className="contentDivs">
                homepage
            </div>

            <div data-item="aboutme" className="contentDivs">
                about me
            </div>
            <div data-item="education" className="contentDivs">
                <h3>My Education</h3>
                <div>
                    <p>Completed three years of high school at the Liceo Scientifico A. Romita, Campobasso, Italy.</p>
                    <p>Graduated from Bishop Grimes Jr./Sr. High School in Syracuse, NY.</p>
                    <p>3rd Year Student at the Rochester Institute of Technology, class of 2022.</p>
                    <p>Pursuing a Bachelor of Science in Web and Mobile Computing and a minor in Computer Security.</p>
                    <p>Received the RIT's Founder Scholarship for Internation Students.</p>
                    <p>Awarded the B. Thomas Golisano College of Computing and Information Sciences Dean's List (Fall 2018, Spring 2019, Fall 2019, Spring 2020).</p>
                </div>
                <h4>
                    <a href= {Resume} target="_blank" rel="noopener noreferrer">Resume</a>
                </h4>
                
            </div>
            <div data-item="work" className="contentDivs">

                <Carousel activeIndex={index} onSelect={handleSelect}>
                    <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src=""
                        alt="First slide" 
                        
                    />
                    <Carousel.Caption></Carousel.Caption>
                    </Carousel.Item>
                </Carousel>
            </div>
            
        </Container>
    );
}

export default ContentContainer;
