import React from 'react';
import { useEffect } from 'react';
import { Card, Container } from 'react-bootstrap';
import './Style.css';

function ContentContainer({ navSelected }) {

    useEffect(() => {
        var divContainer = document.getElementById("content_container");
        var divs = divContainer.getElementsByClassName("contentDivs");

        for (var i = 0; i < divs.length; i++) {
            if (divs[i].dataset.item !== navSelected) {
                divs[i].classList.add("hiddenDivs");
                
            }

            else {
                divs[i].classList.remove("hiddenDivs");
                divs[i].style.color = "red"
            }
        }
      });

    return (
        <Container id="content_container">
            <div data-item="home" className="contentDivs">
                homepage
            </div>

            <div data-item="aboutme" className="contentDivs">
                about me
            </div>
            <div data-item="education" className="contentDivs">
                <h3>My Education</h3>
                <div>
                    <p>I am a 3rd Year Student at the Rochester Institute of Technology, class of 2022.</p>
                    <p>I am pursuing a Bachelor of Science in Web and Mobile Computing.</p>
                    <p>I am also pursuing a minor in Computer Security. I want to learn more about Front-End injections and how to develop secure web applications.</p>
                    <p>I received the RIT's Founder Scholarship for Internation Students.</p>
                    <p>I have been awarded the B. Thomas Golisano College of Computing and Information Sciences Dean's List (Fall 2018, Spring 2019, Fall 2019, Spring 2020).</p>
                </div>
                <h4>Resume</h4>
            </div>
            <div data-item="work" className="contentDivs">
                work
            </div>
            
        </Container>
    );
}

export default ContentContainer;
