import React from 'react';
import { useRef, useState } from 'react';
import { Overlay } from 'react-bootstrap';
import { FaBriefcase, FaGraduationCap, FaHome, FaUser } from 'react-icons/fa';
import './Style.css';

function NavigationItems({ setNavSelected }) {

    const [show, setShow] = useState(false);
    const target = useRef(null);

    const getClickeditem = e => {
        if (e.target.dataset.item !== undefined) {
            setNavSelected(e.target.dataset.item);
        }
    }

    return (
        <ul className="navigation" id="nav_items">
            <li data-item="home" onClick={getClickeditem.bind(this)} onMouseEnter={() => setShow(!show)}>
                <FaHome size={32} data-item="home" color={"#2c4269"}/>
            </li>
            <Overlay target={target.current} show={show} placement="right">
            {({ placement, arrowProps, show: _show, popper, ...props }) => (
          <div
            {...props}
            style={{
              backgroundColor: 'rgba(255, 100, 100, 0.85)',
              padding: '2px 10px',
              color: 'white',
              borderRadius: 3,
              ...props.style,
            }}
          >
            Simple tooltip
          </div>
        )}
            </Overlay>
            <li data-item="aboutme" onClick={getClickeditem.bind(this)}>
                <FaUser size={32} data-item="aboutme" color={"#2c4269"}/>
            </li>
            <li data-item="education" onClick={getClickeditem.bind(this)}>
                <FaGraduationCap size={32} data-item="education" color={"#2c4269"}/>
            </li>
            <li data-item="work" onClick={getClickeditem.bind(this)}>
                <FaBriefcase size={32} data-item="work" color={"#2c4269"}/>
            </li>
        </ul>   
    );
}

export default NavigationItems;
